package com.rong.drop.extensionsimport android.app.Activityimport android.content.Intentimport android.os.Bundle/** * Created by chen.huarong on 2019-05-28. */fun Activity.dropStartActivity(clazz: Class<Any>) {    this.startActivity(Intent(this, clazz))}fun Activity.dropStartActivity(clazz: Class<Any>, bundle: Bundle) {    val intent = Intent(this, clazz)    intent.putExtras(bundle)    this.startActivity(intent)}fun Activity.dropStartActivityForResult(clazz: Class<Any>, requestCode: Int) {    this.startActivityForResult(Intent(this, clazz), requestCode)}fun Activity.dropStartActivityForResult(clazz: Class<Any>, bundle: Bundle, requestCode: Int) {    val intent = Intent(this, clazz)    intent.putExtras(bundle)    this.startActivityForResult(intent, requestCode)}