package com.rong.drop.framework.extensionsimport android.app.Activityimport android.content.Intentimport android.os.Bundleimport android.support.v7.app.AppCompatActivityimport android.widget.Toast/** * Created by chen.huarong on 2019-05-28. */fun Activity.dropStartActivity(clazz: Class<out Activity>) {    this.startActivity(Intent(this, clazz))}fun Activity.dropStartActivity(clazz: Class<Any>, bundle: Bundle) {    val intent = Intent(this, clazz)    intent.putExtras(bundle)    this.startActivity(intent)}fun Activity.dropStartActivityForResult(clazz: Class<Any>, requestCode: Int) {    this.startActivityForResult(Intent(this, clazz), requestCode)}fun Activity.dropStartActivityForResult(clazz: Class<Any>, bundle: Bundle, requestCode: Int) {    val intent = Intent(this, clazz)    intent.putExtras(bundle)    this.startActivityForResult(intent, requestCode)}fun AppCompatActivity.makeToast(value: String) {    Toast.makeText(this.applicationContext, value, Toast.LENGTH_SHORT).show()}